import Util from '@antv/g6/src/util';
import eventBus from "@/utils/eventBus";
export default {
  getDefaultCfg: function getDefaultCfg() {
    return {
      multiple: true,
      keyCode: 16
    };
  },
  getEvents: function getEvents() {
    return {
      'node:click': 'onClick',
      'canvas:click': 'onCanvasClick',
      'canvas:mouseover': 'onCanvasMouseover',
      keyup: 'onKeyUp',
      keydown: 'onKeyDown'
    };
  },
  onClick: function onClick(e) {
    var self = this;
    var item = e.item;
    var graph = self.graph;
    var autoPaint = graph.get('autoPaint');
    graph.setAutoPaint(false);
    var selectedEdges = graph.findAllByState('edge', 'selected');
    Util.each(selectedEdges, function (edge) {
      graph.setItemState(edge, 'selected', false);
    });

    if (!self.keydown || !self.multiple) {
      var selected = graph.findAllByState('node', 'selected');
      Util.each(selected, function (node) {
        if (node !== item) {
          graph.setItemState(node, 'selected', false);
        }
      });
    }

    if (item.hasState('selected')) {
      if (self.shouldUpdate.call(self, e)) {
        graph.setItemState(item, 'selected', false);
      }

      eventBus.$emit('nodeselectchange', {
        target: item,
        select: false
      });
    } else {
      if (self.shouldUpdate.call(self, e)) {
        graph.setItemState(item, 'selected', true);
      }

      eventBus.$emit('nodeselectchange', {
        target: item,
        select: true
      });
    }

    graph.setAutoPaint(autoPaint);
    graph.paint();
  },
  onCanvasClick: function onCanvasClick() {
    var graph = this.graph;
    var autoPaint = graph.get('autoPaint');
    graph.setAutoPaint(false);
    var selected = graph.findAllByState('node', 'selected');
    Util.each(selected, function (node) {
      graph.setItemState(node, 'selected', false);
      eventBus.$emit('nodeselectchange', {
        target: node,
        select: false
      });
    });
    var selectedEdges = graph.findAllByState('edge', 'selected');
    Util.each(selectedEdges, function (edge) {
      graph.setItemState(edge, 'selected', false);
      eventBus.$emit('nodeselectchange', {
        target: edge,
        select: false
      });
    });
    graph.paint();
    graph.setAutoPaint(autoPaint);
  },
  onCanvasMouseover: function onCanvasMouseover() {
    var graph = this.graph;
    graph.paint();
  },
  onKeyDown: function onKeyDown(e) {
    var code = e.keyCode || e.which;

    if (code === this.keyCode) {
      this.keydown = true;
    } else {
      this.keydown = false;
    }
  },
  onKeyUp: function onKeyUp() {
    this.keydown = false;
  }
};
